mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT c.comp_num, comp_name, comp_photo, comp_photoname, comp_content, comp_cate, comp_score FROM company_detail c LEFT OUTER JOIN (SELECT c.comp_num as comp_num, round(avg(comp_score),2) as comp_score FROM company_detail c JOIN food_review r ON c.comp_num=r.comp_num WHERE c.comp_cate=#{comp_cate} GROUP BY c.comp_num) r ON c.comp_num=r.comp_num
ORDER BY comp_score ASC
ORDER BY comp_score DESC
)a)
WHERE rnum >= #{start} AND rnum <= #{end}
